mapper.select:
select member_password
from member
where member_id = #{member_id}

select nvl(count(*),0) 
from member
where member_id = #{member_id}

mapper.update:
update member set member_password=#{member_password}, member_email=#{member_email}, member_name=#{member_name}, member_birthdate=#{member_birthdate}, member_gender=#{member_gender},
member_zipcode=#{member_zipcode}, member_address=#{member_address}
where member_id=#{member_id}

mapper.select:
select member_id
from member
where member_name=#{member_name} and member_email=#{member_email}

select member_password
from member
where member_id=#{member_id} and member_email=#{member_email} and member_phonenumber=#{member_phonenumber}
